sys_relationship.query_with:
(function refineQuery(current, parent) {

	//create a date/time obj
	var time = new GlideDateTime();
	//set the value to user's preferred departure time
	time.setValue(parent.u_when);
	//create a +/- time intervall for the user = 84 mins
	var timeMax = new GlideDateTime();
	time.addSeconds(3600);
	timeMax.setValue(time);

	var timeMin = new GlideDateTime();
	time.addSeconds(-7200);
	timeMin.setValue(time);

	current.addQuery('u_to', parent.u_to);
	current.addQuery('u_departure', '<', timeMax);
	current.addQuery('u_departure', '>', timeMin);
	current.addQuery('u_seats_free', '>=', parent.u_howmany);
})(current, parent);
